(function(){
    var app = angular.module('form-comment', []);
    
    app.directive('formComment', function(){
    return {
        restrict: 'E',
        templateUrl: 'node_modules/formComment/form-comment.html',
        controller: function(){
                this.comment = {};
                this.addComment = function(topic){
                    this.comment.author = "blabla";
                    this.comment.createdOn = Date.now();
                    topic.comments.push(this.comment);
                    this.comment = {};
                };
            },
        controllerAs: 'commentCtrl'
        };
    });
    
    
})();